// ===== HELPERS =====

/**
 * Obter todas as mídias estruturadas por categoria
 */
export const getAllMedia = () => ({
  profissionais: PROFISSIONAIS,
  procedimentos: PROCEDIMENTOS,
  antes_depois: ANTES_DEPOIS,
  tecnologias: TECNOLOGIAS,
  ambiente: AMBIENTE,
  certificados: CERTIFICADOS,
  videos: VIDEOS,
  branding: BRANDING,
})

/**
 * Obter fotos de um procedimento específico
 */
export const getProcedimentoFotos = (procedimentoId: string) => {
  return ANTES_DEPOIS.filter(
    (item) => item.procedimento.toLowerCase().includes(procedimentoId)
  )
}

/**
 * Obter fotos de um profissional específico
 */
export const getProfissionalFotos = (profissionalNome: string) => {
  return ANTES_DEPOIS.filter(
    (item) => item.profissional === profissionalNome
  )
}

/**
 * Gerar lista de todas as fotos para lazy loading
 */
export const getAllImages = (): string[] => {
  const images: string[] = []

  // Profissionais
  Object.values(PROFISSIONAIS).forEach((p) => images.push(p.foto))

  // Procedimentos
  Object.values(PROCEDIMENTOS).forEach((p) => images.push(p.foto))

  // Antes/Depois
  ANTES_DEPOIS.forEach((item) => {
    images.push(item.antes, item.depois)
  })

  // Tecnologias
  Object.values(TECNOLOGIAS).forEach((t) => images.push(t.foto))

  // Ambiente
  Object.values(AMBIENTE).forEach((a) => images.push(a))

  // Certificados
  Object.values(CERTIFICADOS).forEach((c) => images.push(c))

  // Vídeos
  Object.values(VIDEOS).forEach((v) => images.push(v.thumbnail))

  // Branding
  Object.values(BRANDING).forEach((b) => {
    if (typeof b === 'string') images.push(b)
  })

  return images
}

/**
 * Contagem de mídias por tipo
 */
export const getMediaStats = () => {
  return {
    profissionais: Object.keys(PROFISSIONAIS).length,
    procedimentos: Object.keys(PROCEDIMENTOS).length,
    antes_depois: ANTES_DEPOIS.length,
    tecnologias: Object.keys(TECNOLOGIAS).length,
    certificados: Object.keys(CERTIFICADOS).length,
    videos: Object.keys(VIDEOS).length,
    total: getAllImages().length,
  }
}
